CREATE TABLE IF NOT EXISTS ab_tests (id VARCHAR(36) PRIMARY KEY, name VARCHAR(100) NOT NULL, description TEXT, template_id VARCHAR(36) NOT NULL, control_version_id VARCHAR(36) NOT NULL, variant_version_id VARCHAR(36) NOT NULL, status VARCHAR(20) NOT NULL, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, created_by VARCHAR(36) NOT NULL);
